public class Pusher extends Subsystem {

    // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=CONSTANTS

    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=CONSTANTS

    // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=DECLARATIONS
    private final AnalogPotentiometer liftPoten = RobotMap.pusherLiftPoten;
    private final CANTalon liftAngleDriver = RobotMap.pusherLiftAngleDriver;
    private final DigitalInput pusherUpperLimit = RobotMap.pusherPusherUpperLimit;
    private final DigitalInput pusherLowerLimit = RobotMap.pusherPusherLowerLimit;

    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=DECLARATIONS
    
    private PIDController pid;
    private boolean isManualDriveAllowed;
    private static final double maxAngle = 270;
    private static final double kScaleNativeToDegree = maxAngle / 1023.0;
    private static final double kScaleDegreeToNative = 1023.0 / maxAngle;
    private static final int kAllowableError = (int)(Math.round(10 * kScaleDegreeToNative));

    // Put methods for controlling this subsystem
    // here. Call these from Commands.

    public void initDefaultCommand() {
        // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=DEFAULT_COMMAND

        setDefaultCommand(new PusherManageDrive());

    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=DEFAULT_COMMAND

        /*pid = new PIDController(0.05, 0, 0, liftPoten, liftAngleDriver);
        pid.setInputRange(0, 270);
        pid.setOutputRange(-1.0, 1.0);
		pid.setSetpoint(0);*/
        liftAngleDriver.setPID(2.25, 0.00075, 0.175);
        liftAngleDriver.setPIDSourceType(PIDSourceType.kDisplacement);
        liftAngleDriver.setAllowableClosedLoopErr(kAllowableError);
        liftAngleDriver.setFeedbackDevice(FeedbackDevice.AnalogPot);
        setTargetPos(0);
		
		isManualDriveAllowed = true;
        
        // Set the default command for a subsystem here.
        // setDefaultCommand(new MySpecialCommand());
    }
    
    public void setTargetPos(double angle)
    {
    	//pid.setSetpoint(angle);
    	//0-270 -> 0-1023
    	liftAngleDriver.setSetpoint(angle * kScaleDegreeToNative);
    }
    
    public double potValue(){
	  	//return liftPoten.get();
    	return 25;//liftAngleDriver.getAnalogInRaw() * kScaleNativeToDegree;
    }
    
    public boolean isUpperLimitPressed()
    {
    	return pusherUpperLimit.get();
    }
    
    public boolean isLowerLimitPressed()
    {
    	return pusherLowerLimit.get();
    }
    
	public void articulateDown(double speed){
		double potValue = potValue();
		//allow 5deg error
		if(Robot.oi.getDriveJS().getRawButton(7) || (potValue < maxAngle - 2 && !isUpperLimitPressed()))
		{
			liftAngleDriver.set(speed);
		}
		else
		{
			stop();
		}
	}
	public void articulateUp(double speed){
		if(Robot.oi.getDriveJS().getRawButton(7) || (potValue() > 2 && !isLowerLimitPressed()))
		{
			liftAngleDriver.set(speed);
		}
		else
		{
			stop();
		}
	}
	
	public void PIDDriveToSetpoint()
	{
    	SmartDashboard.putNumber("PIDCurPow", liftAngleDriver.getOutputVoltage());
    	SmartDashboard.putNumber("PIDErr", liftAngleDriver.getClosedLoopError() * kScaleNativeToDegree);
		/*if(!pid.isEnabled())
		{
			pid.enable();
		}*/
    	TalonControlMode mode = liftAngleDriver.getControlMode();
    	if(mode != TalonControlMode.Position)
    	{
    		liftAngleDriver.changeControlMode(TalonControlMode.Position);
        	liftAngleDriver.reverseOutput(false);
        	liftAngleDriver.clearIAccum();
    	}
    	liftAngleDriver.set(liftAngleDriver.getSetpoint());
		isManualDriveAllowed = false;
	}
	public boolean isPIDDone()
	{
		double closedLoopErr = Math.abs(liftAngleDriver.getClosedLoopError());
		double vPow = Math.abs(liftAngleDriver.getOutputVoltage());
		if(vPow < 1.0 && closedLoopErr < kAllowableError)
		{
			return true;
		}
		
		return false;
	}
	
	public void endPIDDrive()
	{
		//pid.disable();
		liftAngleDriver.changeControlMode(TalonControlMode.PercentVbus);
		liftAngleDriver.reverseOutput(true);
		isManualDriveAllowed = true;
	}
	
	public void drive(double speed)
	{
		if(!isManualDriveAllowed) return;
		speed *= -0.5;
		if(speed > 0)
		{
			articulateUp(speed);
		}
		else if(speed < 0)
		{
			articulateDown(speed);
		}
		else
		{
			stop();
		}
	}
	public void stop()
	{
		liftAngleDriver.set(0);
	}
}

